import { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const CLIENT_ID = "c38ca31d770c426cb3f1c5219a524734"
  const REDIRECT_URI = "http://localhost:3000"
  const AUTH_ENDPOINT = "https://accounts.spotify.com/authorize"
  const RESPONSE_TYPE = "token"

  const [token, setToken] = useState("")
  const [searchKey, setSearchKey] = useState("")
  const [artists, setArtists] = useState([])

  useEffect(() => {
    const authOptions = {
      url: 'https://accounts.spotify.com/api/token',
      headers: {
        'Authorization': 'Basic ' + Buffer.from(clientId + ':' + clientSecret).toString('base64')
      },
      form: {
        grant_type: 'client_credentials'
      },
      json: true
    };

    axios.post(authOptions.url, authOptions.form, { headers: authOptions.headers })
      .then(response => {
        if (response.status === 200) {
          const token = response.data.access_token;
          setToken(token);
        }
      })
      .catch(error => {
        // Gérer les erreurs ici...
      });
  }, [])

  const searchArtists = async (e) => {
    e.preventDefault()
    const { data } = await axios.get("https://api.spotify.com/v1/search", {
      headers: {
        Authorization: `Bearer ${token}`
      },
      params: {
        q: searchKey,
        type: "artist"
      }
    })

    setArtists(data.artists.items)
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>Spotify React</h1>
      </header>
      <div>
        {token && <div><form onSubmit={searchArtists}>
          <input type="text" onChange={e => setSearchKey(e.target.value)} />
          <button type={"submit"}>Search</button>
        </form>
          {artists.map(artist => (
            <div key={artist.id}>
              {artist.images.length ? <img width={"10%"} src={artist.images[0].url} alt="" /> : <div>No Image</div>}
              {artist.name}
            </div>
          ))}
        </div>
        }
      </div>
    </div>
  );
}

export default App;
