import { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [token, setToken] = useState("")
  const [searchKey, setSearchKey] = useState("")
  const [playlists, setplaylists] = useState([])

  useEffect(() => {
    const client_id = process.env.REACT_APP_CLIENT_ID;
    console.log('client ID = ' + client_id);
    const authOptions = {
      url: 'https://accounts.spotify.com/api/token',
      headers: {
        'Authorization': 'Basic ' + btoa(process.env.REACT_APP_CLIENT_ID + ':' + process.env.REACT_APP_CLIENT_SECRET),
        'Content-Type': 'application/x-www-form-urlencoded'
      },
      form: {
        grant_type: 'client_credentials'
      },
      json: true
    };

    axios.post(authOptions.url, authOptions.form, { headers: authOptions.headers })
      .then(response => {
        if (response.status === 200) {
          const token = response.data.access_token;
          setToken(token);
        }
      })
      .catch(error => {
        console.log(error);
      });
  }, [])

  const searchplaylists = async (e) => {
    e.preventDefault()
    const { data } = await axios.get("https://api.spotify.com/v1/search", {
      headers: {
        Authorization: `Bearer ${token}`
      },
      params: {
        q: searchKey,
        type: "playlist"
      }
    })
    console.log(JSON.stringify(data));
    setplaylists(data.playlists.items)
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>Spotify React</h1>
      </header>
      <div>
        {token && <div>
          <form onSubmit={searchplaylists}>
            <input type="text" onChange={e => setSearchKey(e.target.value)} />
            <button type={"submit"}>Search</button>
          </form>
          {playlists.map(playlist => (
            <div key={playlist.id}>
              {playlist.images.length ? <img width={"10%"} src={playlist.images[0].url} alt="" /> : <div>No Image</div>}
              {playlist.name}
            </div>
          ))}
        </div>
        }
      </div>
    </div>
  );
}

export default App;
