export default function OptionBar({option, setOption}) {
    return (
        <li style={{display: "flex"}} >
            <ul style={option === "playlist" ? {color: 'green'} : {color: 'black'}} onClick={() => setOption("playlist")}>Playlists</ul>
            <ul style={option === "artist" ? {color: 'green'} : {color: 'black'}} onClick={() => setOption("artist")}>Artists</ul>
            <ul style={option === "album" ? {color: 'green'} : {color: 'black'}} onClick={() => setOption("album")}>albums</ul>
            <ul style={option === "track" ? {color: 'green'} : {color: 'black'}} onClick={() => setOption("track")}>Titres</ul>
        </li>
    )
}
